--
-- Структура таблицы `stock`
--

CREATE TABLE IF NOT EXISTS `stock` (
`id` int(11) NOT NULL,
  `title` varchar(150) NOT NULL,
  `text` text NOT NULL,
  `on` int(1) NOT NULL DEFAULT '1'
) ENGINE=InnoDB AUTO_INCREMENT=9 DEFAULT CHARSET=utf8;

--
-- Дамп данных таблицы `stock`
--

INSERT INTO `stock` (`id`, `title`, `text`, `on`) VALUES
(1, 'Скидка сезона', '<p>Коронка металлокерамическая - 1250грн<br>\r\nПротез акриловый - 3000грн<br>\r\nЧистка зубов - 300грн<br>\r\nПломба фотополимерная - 300грн</p>', 1),
(2, 'Запишись на прием к доктору на сайте, получи 5% скидку', 'Акция "Запишись на прием к доктору на сайте, получи 5% скидку" проводится бессрочно и действует, если Вы записываетесь на прием с нашего сайта Вы получаете скидку 5% на любые услуги нашего кабинета.', 1),
(3, 'Стоматологическое меню', '<p>Акция "Стоматологическое меню" \r\nпроводится бессрочно и суть ее такова, что есть определенные дни недели, когда действуют скидки на определенные виды услуг.\r\nА именно:</p>\r\n<ul> \r\n <li>Вторник и четвер<br>с 8-00 до 11-00 - Чистка зубов и Удаление корней - скидка 10%</li>\r\n <li>с 11-00 до 13-00 - Протез на 1 челюсть - скидка 5%</li>\r\n</ul>', 1),
(4, 'Приведи друга и получите вдвоем СКИДКУ', '<p>Акция "Приведи друга и получите вдвоем СКИДКУ" проводится бессрочно и действует, если Вы приводите своего друга, или кого-нибудь, кому нужна наша помощь. Вы получаете скидку 7% на лечение.</p>', 1),
(5, 'Сделай 100% предоплату на протезирование и получи скидку', '<p>Акция "Сделай 100% предоплату на протезирование и получи скидку" проводится бессрочно. Все Вы наверное знаете, что протезирование довольно таки дорого стоит. По этому многие выходят из данной ситуации проплачивая стоимость лечения частями. Но, если Вы найдете возможность сделать 100% предоплату, то получите сразу скидку 3%.</p>', 1),
(6, 'Запротезируй две челюсти и получи в подарок бокс для хранения протезов', '<p>Акция "Запротезируй две челюсти и получи в подарок бокс для хранения протезов" проводится бессрочно. Очень удобно хранить зубные протезы в специальном боксе для хранения протезов. Такой бокс Вы можете получить в подарок, запротезировав у нас две челюсти.</p>', 1),
(7, 'Проект помощи полностью беззубому человеку', '<p>Распространяется на социальную группу населения: инвалиды, ветераны войны и боевых действий, дети войны, малоимущие, пострадавшие от ЧАЭС. Пациент выбирается на основании отбора, после предоставления панорамного снимка и карты пациента с историей. БЕСПЛАТНО протезируется один человек в месяц.</p>', 1),
(8, 'СКИДКА 20% на профессиональную гигиену полости рта в канун вашего дня рождения! Только для наших пациентов!!!', '<p>Акция проводится бессрочно и действует, 7 дней до дня рождения и 7 дней после дня рождения. Одно обязательно требование, предъявление документа с указанием даты рождения.</p>', 1);

